task_pane.script:
function UnHide( eThis ){
    if( eThis.firstChild.innerHTML.charCodeAt(0) == 9658 ){
        eThis.firstChild.innerHTML = '▼'
        eThis.parentNode.parentNode.className = '';
    }else{
        eThis.firstChild.innerHTML = '►'
        eThis.parentNode.parentNode.className = 'cl';
    }
    return false;
}
